import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import Button from '../components/Button';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4rem;
`;

const HeroSection = styled.section`
  background: url('/assets/images/banner-home.webp') no-repeat center center/cover;
  color: var(--background);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 4rem 2rem;
  min-height: 100vh;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(19, 53, 43, 0.7); /* Overlay escuro */
    z-index: 1;
  }

  h1,
  button {
    position: relative;
    z-index: 2;
  }

  h1 {
    font-size: 3rem;
    margin-bottom: 1.5rem;

    @media (max-width: 768px) {
      font-size: 2.4rem;
    }
  }
`;

const AboutSection = styled(motion.section)`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  padding: 4rem 2rem;
  gap: 2rem;
  background: var(--background);
  color: var(--dark-green);

  h2 {
    font-size: 2.5rem;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  p {
    max-width: 600px;
    font-size: 1.2rem;
    line-height: 1.8;

    @media (max-width: 768px) {
      font-size: 1rem;
    }
  }
`;

const ServicesSection = styled(motion.section)`
  background: var(--background);
  color: var(--dark-green);
  text-align: center;
  padding: 4rem 2rem;

  h2 {
    font-size: 2.5rem;
    margin-bottom: 2rem;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }
`;

const CardsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
  }
`;

const ServiceCard = styled.div`
  background: var(--light-green);
  padding: 2rem;
  border-radius: 10px;
  max-width: 300px;
  flex: 1;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }

  p {
    font-size: 1rem;
    line-height: 1.5;
  }
`;

const animationVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0 },
};

const Home = () => {
  return (
    <HomeContainer>
      {/* Hero Section */}
      <HeroSection>
        <h1>Transformando Resíduos em Recursos Valiosos</h1>
        <Link to="/contato">
          <Button>Fale Conosco</Button>
        </Link>
      </HeroSection>

      {/* About Section */}
      <AboutSection
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.6 }}
        variants={animationVariants}
      >
        <h2>Sobre a Recoopera</h2>
        <p>
          Somos uma empresa comprometida com a sustentabilidade, oferecendo soluções inteligentes
          para a gestão de resíduos. Nosso objetivo é transformar resíduos em recursos valiosos.
        </p>
        <Link to="/sobre">
          <Button>Saiba Mais</Button>
        </Link>
      </AboutSection>

      {/* Services Section */}
      <ServicesSection
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.6 }}
        variants={animationVariants}
      >
        <h2>Nossos Serviços</h2>
        <CardsContainer>
          <ServiceCard>
            <h3>Gestão de Resíduos Recorrente</h3>
            <p>Coleta eficiente e regular para o seu negócio.</p>
          </ServiceCard>
          <ServiceCard>
            <h3>Gestão de Resíduos para Eventos</h3>
            <p>Planejamento e execução sustentável para eventos.</p>
          </ServiceCard>
        </CardsContainer>
        <Link to="/servicos">
          <Button>Conheça Mais</Button>
        </Link>
      </ServicesSection>
    </HomeContainer>
  );
};

export default Home;
