import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4rem;
`;

const HeroSection = styled(motion.section)`
  background: url('./assets/images/banner-home.webp') no-repeat center center/cover;
  color: var(--background);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 4rem 2rem;
  min-height: 100vh;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5); /* Overlay escuro */
    z-index: 1;
  }

  h1,
  p,
  button {
    position: relative;
    z-index: 2;
  }

  h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  button {
    padding: 0.75rem 2rem;
    background: var(--primary-green);
    color: var(--background);
    font-weight: bold;
    border-radius: 5px;
    cursor: pointer;
    border: none;
    transition: background 0.3s ease;

    &:hover {
      background: var(--dark-green);
    }
  }
`;

const ServicesSection = styled(motion.section)`
  background: var(--background);
  color: var(--dark-green);
  text-align: center;
  padding: 4rem 2rem;

  h2 {
    font-size: 2rem;
    margin-bottom: 2rem;
  }

  div {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 2rem;

    @media (max-width: 768px) {
      flex-direction: column;
    }

    div {
      background: var(--light-green);
      padding: 1.5rem;
      border-radius: 10px;
      max-width: 300px;
      flex: 1;
      text-align: left;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      transition: transform 0.3s ease;

      &:hover {
        transform: scale(1.05);
      }

      h3 {
        font-size: 1.5rem;
        margin-bottom: 0.5rem;
      }

      p {
        font-size: 1rem;
        line-height: 1.5;
      }
    }
  }
`;

const animationVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0 },
};

const Home = () => {
  return (
    <HomeContainer>
      {/* Hero Section */}
      <HeroSection
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.6 }}
        variants={animationVariants}
      >
        <h1>Transformando Resíduos em Recursos Valiosos</h1>
        <Link to="/contato">
          <button>Fale Conosco</button>
        </Link>
      </HeroSection>

      {/* Services Section */}
      <ServicesSection
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.2 }}
        transition={{ duration: 0.6, staggerChildren: 0.2 }}
        variants={animationVariants}
      >
        <h2>Nossos Serviços</h2>
        <div>
          <div>
            <h3>Gestão de Resíduos Recorrente</h3>
            <p>Coleta eficiente e regular para o seu negócio.</p>
          </div>
          <div>
            <h3>Gestão de Resíduos para Eventos</h3>
            <p>Planejamento e execução sustentável para eventos.</p>
          </div>
        </div>
      </ServicesSection>
    </HomeContainer>
  );
};

export default Home;
