import React from 'react';
import styled from 'styled-components';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4rem;
`;

const HeroSection = styled.section`
  background: var(--dark-background);
  color: var(--background);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 4rem 2rem;
  height: 100vh;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    color: var(--light-green);
  }

  p {
    font-size: 1.25rem;
    margin-bottom: 2rem;
  }

  button {
    padding: 0.75rem 2rem;
    background: var(--primary-green);
    color: var(--background);
    font-weight: bold;
    border-radius: 5px;
    cursor: pointer;

    &:hover {
      background: var(--dark-green);
    }
  }
`;

const SolutionsSection = styled.section`
  background: var(--background);
  color: var(--dark-green);
  text-align: center;
  padding: 4rem 2rem;

  h2 {
    font-size: 2rem;
    margin-bottom: 2rem;
  }

  div {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 2rem;

    @media (max-width: 768px) {
      flex-direction: column;
      gap: 1rem;
    }

    div {
      background: var(--light-green);
      padding: 1.5rem;
      border-radius: 10px;
      max-width: 300px;
      flex: 1;
      text-align: left;

      h3 {
        font-size: 1.5rem;
        margin-bottom: 0.5rem;
      }

      p {
        font-size: 1rem;
        line-height: 1.5;
      }
    }
  }
`;

const ImpactSection = styled.section`
  background: var(--dark-background);
  color: var(--background);
  text-align: center;
  padding: 4rem 2rem;

  h2 {
    font-size: 2rem;
    margin-bottom: 2rem;
  }

  div {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 2rem;

    div {
      background: var(--dark-green);
      padding: 1.5rem;
      border-radius: 10px;
      max-width: 200px;

      h3 {
        font-size: 1.5rem;
        margin-bottom: 0.5rem;
      }

      p {
        font-size: 1rem;
      }
    }
  }
`;

const Home = () => {
  return (
    <HomeContainer>
      {/* Hero Section */}
      <HeroSection>
        <h1>Transformando Resíduos em Recursos Valiosos</h1>
        <p>Soluções inteligentes para gestão de resíduos, promovendo um futuro sustentável.</p>
        <button>Fale com um Especialista</button>
      </HeroSection>

      {/* Solutions Section */}
      <SolutionsSection>
        <h2>Nossas Soluções</h2>
        <div>
          <div>
            <h3>Gestão de Resíduos Recorrente</h3>
            <p>Coleta regular e eficiente para atender às necessidades do seu negócio.</p>
          </div>
          <div>
            <h3>Gestão de Resíduos para Eventos</h3>
            <p>Planejamento e execução da coleta em eventos, garantindo sustentabilidade.</p>
          </div>
        </div>
      </SolutionsSection>

      {/* Impact Section */}
      <ImpactSection>
        <h2>Nosso Impacto</h2>
        <div>
          <div>
            <h3>100+</h3>
            <p>Clientes Atendidos</p>
          </div>
          <div>
            <h3>500+</h3>
            <p>Toneladas Coletadas</p>
          </div>
          <div>
            <h3>1.2K+</h3>
            <p>Carbono Compensado</p>
          </div>
        </div>
      </ImpactSection>
    </HomeContainer>
  );
};

export default Home;
