import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4rem;
`;

const HeroSection = styled.section`
  background: url('/assets/images/banner-home.webp') no-repeat center center/cover;
  color: var(--background);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 4rem 2rem;
  min-height: 100vh;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(19, 53, 43, 0.7); /* Overlay escuro */
    z-index: 1;
  }

  h1,
  p,
  button {
    position: relative;
    z-index: 2;
  }

  h1 {
    font-size: 3rem;
    margin-bottom: 1.5rem;
    color: var(--light-green);

    @media (max-width: 768px) {
      font-size: 2.4rem;
    }
  }

  button {
    padding: 1rem 2.5rem;
    background: var(--primary-green);
    color: var(--background);
    font-weight: bold;
    border-radius: 5px;
    cursor: pointer;
    border: none;
    transition: background 0.3s ease;

    &:hover {
      background: var(--dark-green);
    }
  }
`;

const ServicesSection = styled.section`
  background: var(--background);
  color: var(--dark-green);
  text-align: center;
  padding: 4rem 2rem;

  @media (max-width: 768px) {
    padding: 3rem 1.5rem;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 2rem;
  color: var(--primary-green);

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const CardsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
  }
`;

const ServiceCard = styled.div`
  background: var(--light-green);
  padding: 2rem;
  border-radius: 10px;
  max-width: 300px;
  flex: 1;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: var(--dark-green);
  }

  p {
    font-size: 1rem;
    line-height: 1.5;
    color: var(--dark-green);
  }
`;

const Home = () => {
  return (
    <HomeContainer>
      {/* Hero Section */}
      <HeroSection>
        <h1>Transformando Resíduos em Recursos Valiosos</h1>
        <Link to="/contato">
          <button>Fale Conosco</button>
        </Link>
      </HeroSection>

      {/* Services Section */}
      <ServicesSection>
        <SectionTitle>Nossos Serviços</SectionTitle>
        <CardsContainer>
          <ServiceCard>
            <h3>Gestão de Resíduos Recorrente</h3>
            <p>Coleta eficiente e regular para o seu negócio.</p>
          </ServiceCard>
          <ServiceCard>
            <h3>Gestão de Resíduos para Eventos</h3>
            <p>Planejamento e execução sustentável para eventos.</p>
          </ServiceCard>
        </CardsContainer>
      </ServicesSection>
    </HomeContainer>
  );
};

export default Home;
