import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import Button from '../components/Button';
import heroImage from '../assets/images/banner-home.webp'; // Imagem da Hero Section
import aboutImage from '../assets/images/about.webp'; // Imagem da Seção Sobre

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 6rem;
`;

const HeroSection = styled.section`
  background: url(${heroImage}) no-repeat center center/cover;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  min-height: 100vh;
  color: var(--background);
  position: relative;
  padding: 0 1.5rem;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(19, 53, 43, 0.7); /* Overlay escuro */
    z-index: 1;
  }

  h1 {
    position: relative;
    z-index: 2;
    font-size: 3.5rem;
    color: var(--light-green);
    margin-bottom: 1rem;

    @media (max-width: 768px) {
      font-size: 2.5rem;
    }
  }

  p {
    position: relative;
    z-index: 2;
    font-size: 1.25rem;
    margin-bottom: 2rem;

    @media (max-width: 768px) {
      font-size: 1rem;
    }
  }

  a {
    position: relative;
    z-index: 2;
  }
`;

const AboutSection = styled(motion.section)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 4rem;
  padding: 4rem 2rem;
  background: var(--background); /* Cor de fundo ajustada */
  color: var(--dark-green);

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
    gap: 2rem;
  }
`;

const AboutContent = styled.div`
  flex: 1;

  h2 {
    font-size: 2.5rem;
    color: var(--dark-green);
    margin-bottom: 1.5rem;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }

  p {
    font-size: 1.2rem;
    line-height: 1.8;
    color: var(--dark-green);
    margin-bottom: 2rem;

    @media (max-width: 768px) {
      font-size: 1rem;
    }
  }
`;

const AboutImage = styled.img`
  flex: 1;
  max-width: 500px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

  @media (max-width: 768px) {
    max-width: 100%;
  }
`;

const ServicesSection = styled(motion.section)`
  text-align: center;
  padding: 4rem 2rem;
  background: var(--primary-green);
  color: var(--background);

  h2 {
    font-size: 2.5rem;
    margin-bottom: 2rem;

    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }
`;

const CardsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
  }
`;

const ServiceCard = styled.div`
  background: var(--background);
  padding: 2rem;
  border-radius: 10px;
  max-width: 300px;
  flex: 1;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: var(--dark-green);
  }

  p {
    font-size: 1rem;
    line-height: 1.5;
    color: var(--dark-green);
  }
`;

const animationVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0 },
};

const Home = () => {
  return (
    <HomeContainer>
      {/* Hero Section */}
      <HeroSection>
        <h1>Transformando Resíduos em Recursos Valiosos</h1>
        <p>Contribuímos para um futuro sustentável com soluções inovadoras.</p>
        <Link to="/contato">
          <Button>Fale Conosco</Button>
        </Link>
      </HeroSection>

      {/* About Section */}
      <AboutSection
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.8 }}
        variants={animationVariants}
      >
        <AboutContent>
          <h2>Sobre a Recoopera</h2>
          <p>
            Somos uma empresa comprometida com a sustentabilidade, oferecendo soluções inteligentes
            para a gestão de resíduos. Nosso objetivo é transformar resíduos em recursos valiosos.
          </p>
          <Link to="/sobre">
            <Button backgroundColor="var(--dark-green)">Saiba Mais</Button>
          </Link>
        </AboutContent>
        <AboutImage src={aboutImage} alt="Sobre a Recoopera" />
      </AboutSection>

      {/* Services Section */}
      <ServicesSection
        initial="hidden"
        animate="visible"
        transition={{ duration: 0.8, staggerChildren: 0.2 }}
        variants={animationVariants}
      >
        <h2>Nossos Serviços</h2>
        <CardsContainer>
          <ServiceCard>
            <h3>Gestão de Resíduos Recorrente</h3>
            <p>Coleta eficiente e regular para o seu negócio.</p>
          </ServiceCard>
          <ServiceCard>
            <h3>Gestão de Resíduos para Eventos</h3>
            <p>Planejamento e execução sustentável para eventos.</p>
          </ServiceCard>
        </CardsContainer>
        <Link to="/servicos">
          <Button backgroundColor="var(--dark-green)">Conheça Mais</Button>
        </Link>
      </ServicesSection>
    </HomeContainer>
  );
};

export default Home;
